package io.github.mingchoi.leetcodesolutionjava.solution.q5_longest_palindromic_substring;

import org.junit.Test;

import static org.junit.Assert.*;

public class SolutionTest {

    @Test
    public void isPalindrome() {
        class TestCase {
            public boolean expected;
            public String s;

            public TestCase(boolean ex, String s) {
                this.expected = ex;
                this.s = s;
            }
        }

        TestCase[] cases = new TestCase[]{
                new TestCase(true, "babab"),
                new TestCase(false, "babad"),
                new TestCase(true, "aba"),
                new TestCase(true, "cc"),
                new TestCase(true, "abba"),
                new TestCase(true, "z"),
        };

        for (TestCase c : cases) {
            assertEquals(c.expected, new Solution().isPalindrome(c.s));
        }
    }

    @Test
    public void longestPalindrome() {
        class TestCase {
            public String expected;
            public String s;

            public TestCase(String s, String ex) {
                this.expected = ex;
                this.s = s;
            }
        }

        TestCase[] cases = new TestCase[]{
                new TestCase("babad", "aba"),
                new TestCase("cbbd", "bb"),
                new TestCase("abcba", "abcba"),
                new TestCase("a", "a"),
                new TestCase("bb", "bb"),
                new TestCase("aaaaa", "aaaaa"),
                new TestCase("aaaaaa", "aaaaaa"),
                new TestCase("binxacai", "aca"),
                new TestCase("",
                        ""),
                new TestCase("",
                        "qahaq")
        };

        for (TestCase c : cases) {
            assertEquals(c.expected, new Solution().longestPalindrome(c.s));
        }
    }
}